import React, { useState, useEffect } from "react";

export default function States({ selectedState, onSelectState, onBack }) {
  const [states] = useState([
    "Andhra Pradesh",
    "Arunachal Pradesh",
    "Assam",
    "Bihar",
    "Chhattisgarh",
    "Goa",
    "Gujarat",
    "Haryana",
    "Himachal Pradesh",
    "Jharkhand",
    "Karnataka",
    "Kerala",
    "Madhya Pradesh",
    "Maharashtra",
    "Manipur",
    "Meghalaya",
    "Mizoram",
    "Nagaland",
    "Odisha",
    "Punjab",
    "Rajasthan",
    "Sikkim",
    "Tamil Nadu",
    "Telangana",
    "Tripura",
    "Uttar Pradesh",
    "Uttarakhand",
    "West Bengal",
    "Delhi",
    "Puducherry",
    "Jammu and Kashmir",
    "Ladakh",
  ]);

  const [currentState, setCurrentState] = useState(selectedState || null);
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  // Edit modal states
  const [editingPlace, setEditingPlace] = useState(null);
  const [editName, setEditName] = useState("");
  const [editDescription, setEditDescription] = useState("");

  useEffect(() => {
    if (currentState) {
      fetch(`http://localhost:8000/places?state=${encodeURIComponent(currentState)}`)
        .then((r) => r.json())
        .then(setPlaces)
        .catch((e) => console.error("Error fetching places:", e));
    }
  }, [currentState]);

  // Filter places by search term
  const filteredPlaces = places.filter((p) =>
    p.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // ‚úÖ Delete place
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this place?")) return;
    try {
      const res = await fetch(`http://localhost:8000/places/${id}`, {
        method: "DELETE",
      });
      if (res.ok) {
        setPlaces((prev) => prev.filter((p) => p.id !== id));
        alert("Place deleted successfully ‚úÖ");
      } else {
        alert("Failed to delete place ‚ùå");
      }
    } catch (err) {
      console.error("Delete error:", err);
    }
  };

  // ‚úÖ Open Edit modal
  const handleEdit = (place) => {
    setEditingPlace(place);
    setEditName(place.name);
    setEditDescription(place.description);
  };

  // ‚úÖ Submit edit
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:8000/places/${editingPlace.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: editName,
          description: editDescription,
        }),
      });

      if (res.ok) {
        const updated = await res.json();
        setPlaces((prev) =>
          prev.map((p) => (p.id === updated.id ? updated : p))
        );
        setEditingPlace(null);
        alert("Place updated successfully ‚úÖ");
      } else {
        alert("Failed to update place ‚ùå");
      }
    } catch (err) {
      console.error("Edit error:", err);
    }
  };

  return (
    <div style={page}>
      <h1 style={heading}>Indian States</h1>

      {/* --- Show all states --- */}
      {!currentState && (
        <div style={buttonGrid}>
          {states.map((st) => (
            <button
              key={st}
              style={stateButton}
              onClick={() => {
                setCurrentState(st);
                if (onSelectState) onSelectState(st);
              }}
              onMouseEnter={(e) => (e.target.style.background = "#0056b3")}
              onMouseLeave={(e) => (e.target.style.background = "#007bff")}
            >
              {st}
            </button>
          ))}
        </div>
      )}

      {/* --- Show places for selected state --- */}
      {currentState && !selectedPlace && (
        <div style={placesSection}>
          <button
            style={backBtn}
            onClick={() => {
              setCurrentState(null);
              if (onBack) onBack();
            }}
          >
            ‚Üê Back to States
          </button>

          <h2 style={stateTitle}>{currentState}</h2>

          {/* üîç Search bar */}
          <input
            type="text"
            placeholder="Search places..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={searchBox}
          />

          {filteredPlaces.length === 0 ? (
            <p style={{ textAlign: "center" }}>No places found.</p>
          ) : (
            <div style={placesGrid}>
              {filteredPlaces.map((p) => (
                <div key={p.id} style={placeCard}>
                  <h3>{p.name}</h3>
                  <p>{p.description?.slice(0, 60)}...</p>
                  {p.photos && p.photos[0] && (
                    <img
                      src={`http://localhost:8000/uploads/${p.photos[0]}`}
                      alt={p.name}
                      style={photoSmall}
                      onClick={() => setSelectedPlace(p)}
                    />
                  )}
                  <div style={btnRow}>
                    <button style={viewBtn} onClick={() => setSelectedPlace(p)}>
                      View
                    </button>
                    <button style={editBtn} onClick={() => handleEdit(p)}>
                      Edit
                    </button>
                    <button style={deleteBtn} onClick={() => handleDelete(p.id)}>
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* --- Show expanded place details --- */}
      {selectedPlace && (
        <div style={placeDetail}>
          <button style={backBtn} onClick={() => setSelectedPlace(null)}>
            ‚Üê Back to {currentState} Places
          </button>

          <h2 style={stateTitle}>{selectedPlace.name}</h2>
          <p style={desc}>{selectedPlace.description}</p>

          {selectedPlace.photos && selectedPlace.photos.length > 0 ? (
            <div style={photosGrid}>
              {selectedPlace.photos.map((ph, i) => (
                <img
                  key={i}
                  src={`http://localhost:8000/uploads/${ph}`}
                  alt={`${selectedPlace.name}-${i}`}
                  style={photoLarge}
                />
              ))}
            </div>
          ) : (
            <p>No photos available.</p>
          )}
        </div>
      )}

      {/* --- Edit Modal --- */}
      {editingPlace && (
        <div style={modalOverlay}>
          <div style={modalBox}>
            <h3>Edit Place</h3>
            <form onSubmit={handleEditSubmit}>
              <input
                type="text"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                placeholder="Place name"
                required
                style={modalInput}
              />
              <textarea
                value={editDescription}
                onChange={(e) => setEditDescription(e.target.value)}
                placeholder="Description"
                rows={4}
                required
                style={modalTextarea}
              />
              <div style={{ marginTop: "10px" }}>
                <button type="submit" style={saveBtn}>
                  Save
                </button>
                <button
                  type="button"
                  onClick={() => setEditingPlace(null)}
                  style={cancelBtn}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

/* ---- STYLES ---- */
