import React, {useState} from 'react';
export default function Login({onLogin}){
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [err, setErr] = useState('');
  function submit(e){ e.preventDefault(); setErr(''); const fd = new FormData(); fd.append('username', username); fd.append('password', password); fetch('http://localhost:8000/login',{method:'POST', body:fd}).then(r=>r.json()).then(data=>{ if(data.detail) setErr(data.detail); else { onLogin(data.token); } }).catch(e=>setErr('Network error')); }
  return (<div style={{maxWidth:420}}><h2>Login</h2><form onSubmit={submit}><input placeholder='username or email' value={username} onChange={e=>setUsername(e.target.value)}/><input placeholder='password' type='password' value={password} onChange={e=>setPassword(e.target.value)}/><button className='btn' type='submit'>Login</button>{err && <div style={{color:'red'}}>{err}</div>}</form></div>)
}
