import React, { useEffect, useState } from "react";

export default function Home({ onSelectState, showState }) {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    fetchPlaces();
  }, []);

  function fetchPlaces(state) {
    const url = state
      ? `http://localhost:8000/places?state=${encodeURIComponent(state)}`
      : "http://localhost:8000/places";
    fetch(url)
      .then((r) => r.json())
      .then(setPlaces)
      .catch((e) => console.error(e));
  }

  useEffect(() => {
    if (showState) fetchPlaces(showState);
  }, [showState]);

  return (
    <div style={pageStyle}>
      <div style={overlay}>
        <h1 style={title}>Welcome to Indian Tourism</h1>
        <p style={subtitle}>Explore the beauty of India â€” one state at a time ðŸ‡®ðŸ‡³</p>

        {/* Places Grid */}
        <div style={grid}>
          {places.map((p) => (
            <div key={p.id} style={card}>
              <h3 style={placeName}>{p.name}</h3>
              <div style={placeState}>{p.state}</div>
              <p style={desc}>{p.description?.slice(0, 120)}</p>
              {p.photos && p.photos[0] && (
                <img
                  src={`http://localhost:8000/uploads/${p.photos[0]}`}
                  alt={p.name}
                  style={photo}
                />
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const pageStyle = {
  backgroundImage:
    "url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR0UKmmu2SO6bjD6Gfb-a-gCppLgQhgXRf16_WeN97UoplZfC2Rdew1A4bCnoFwj9yQVLcz6iUCI-WxTEF7nzq5nzOiSlRn_G50Ol99PNOurg&s=10')",
  backgroundSize: "cover",
  backgroundPosition: "center",
  minHeight: "100vh",
  color: "white",
  position: "relative",
  overflow: "hidden",
};

const overlay = {
  backgroundColor: "rgba(0,0,0,0.5)",
  padding: "40px",
  minHeight: "100vh",
};

const title = {
  textAlign: "center",
  fontSize: "2.5em",
  marginTop: "10px",
  fontWeight: "bold",
};

const subtitle = {
  textAlign: "center",
  fontSize: "1.2em",
  marginBottom: "30px",
  color: "#f0f0f0",
};

const map = {
  position: "relative",
  width: "500px",
  height: "200px",
  margin: "20px auto",
  border: "1px solid rgba(255,255,255,0.3)",
  borderRadius: "10px",
  background: "rgba(255,255,255,0.1)",
};

const stateBtn = {
  position: "absolute",
  padding: "5px 10px",
  background: "rgba(255,255,255,0.8)",
  color: "#333",
  borderRadius: "6px",
  cursor: "pointer",
  fontSize: "12px",
  fontWeight: "bold",
  transition: "all 0.3s",
};
stateBtn[":hover"] = {
  background: "white",
  transform: "scale(1.05)",
};

const grid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
  gap: "20px",
  marginTop: "30px",
};

const card = {
  background: "rgba(255,255,255,0.85)",
  borderRadius: "10px",
  padding: "10px",
  color: "#333",
  boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
};

const placeName = {
  fontSize: "1.2em",
  fontWeight: "bold",
};

const placeState = {
  fontSize: "0.9em",
  color: "#555",
};

const desc = {
  fontSize: "0.9em",
  color: "#444",
  marginTop: "5px",
};

const photo = {
  width: "100%",
  height: "140px",
  borderRadius: "8px",
  marginTop: "8px",
  objectFit: "cover",
};
