import React, { useState } from "react";

export default function Signup({ onSignup, onSwitchToLogin }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [err, setErr] = useState("");
  const [success, setSuccess] = useState("");

  async function submit(e) {
    e.preventDefault();
    setErr("");
    setSuccess("");
    const fd = new FormData();
    fd.append("username", username);
    fd.append("email", email);
    fd.append("password", password);
    if (file) fd.append("file", file);

    try {
      const res = await fetch("http://localhost:8000/signup", {
        method: "POST",
        body: fd,
      });
      const data = await res.json();
      if (data.detail) setErr(data.detail);
      else {
        setSuccess("Signup successful!");
        onSignup(data.token);
      }
    } catch (e) {
      setErr("Network error");
    }
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "80vh",
      }}
    >
      <div
        style={{
          background: "linear-gradient(135deg, #c850c0, #4158d0)",
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 8px 20px rgba(0,0,0,0.2)",
          width: "350px",
          textAlign: "center",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "20px" }}>Create Account</h2>

        <form onSubmit={submit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={inputStyle}
          />
          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={inputStyle}
          />
          <label style={{ color: "white", fontSize: "14px" }}>
            Profile photo (0â€“2MB)
          </label>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            accept="image/*"
            style={{
              ...inputStyle,
              background: "white",
              border: "none",
              color: "#333",
            }}
          />

          <button
            type="submit"
            style={{
              ...inputStyle,
              background: "#fff",
              color: "#333",
              cursor: "pointer",
              fontWeight: "bold",
              marginTop: "10px",
            }}
          >
            Sign Up
          </button>

          {err && <div style={{ color: "yellow", marginTop: "8px" }}>{err}</div>}
          {success && (
            <div style={{ color: "#00ff99", marginTop: "8px" }}>{success}</div>
          )}
        </form>

        <p style={{ color: "white", marginTop: "15px" }}>
          Already have an account?{" "}
          <span
            onClick={onSwitchToLogin}
            style={{ textDecoration: "underline", cursor: "pointer" }}
          >
            Login here
          </span>
        </p>
      </div>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  padding: "10px",
  margin: "8px 0",
  borderRadius: "6px",
  border: "none",
  outline: "none",
};
