import React, { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import Home from "./Home";
import Login from "./Login";
import Signup from "./Signup";
import AddPlace from "./AddPlace";

function App() {
  const [view, setView] = useState("home");
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [selectedState, setSelectedState] = useState(null);
  const [showSignup, setShowSignup] = useState(false);

  useEffect(() => {
    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [token]);

  return (
    <div>
      {/* HEADER */}
      <div className="header" style={headerStyle}>
        <div style={{ fontWeight: "bold", fontSize: 20 }}>
          Welcome to Indian Tourism
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <div
            className="three-lines"
            style={linesStyle}
            onClick={() => alert("Sidebar toggled (mobile)")}
          >
            <div className="line" style={lineStyle}></div>
            <div className="line" style={lineStyle}></div>
            <div className="line" style={lineStyle}></div>
          </div>
          <div style={{ fontSize: 14, fontWeight: 500 }}>
            {token ? "Logged in" : "Not logged"}
          </div>
        </div>
      </div>

      {/* BODY */}
      <div className="container" style={{ display: "flex", minHeight: "90vh" }}>
        {/* SIDEBAR */}
        <Sidebar
          onNavigate={setView}
          token={token}
          onLogout={() => {
            setToken(null);
            setView("home");
          }}
        />

        {/* MAIN CONTENT */}
        <div
          className="content"
          style={{
            flex: 1,
            padding: "20px",
            background: "#fff",
            overflowY: "auto",
          }}
        >
          {view === "home" && (
            <Home
              onSelectState={(s) => {
                setSelectedState(s);
                setView("state");
              }}
              selectedState={selectedState}
            />
          )}

          {view === "login" && (
            <Login
              onLogin={(t) => {
                setToken(t);
                setView("home");
              }}
              onSwitchToSignup={() => {
                setShowSignup(true);
                setView("signup");
              }}
            />
          )}

          {view === "signup" && (
            <Signup
              onSignup={(t) => {
                setToken(t);
                setView("home");
              }}
              onSwitchToLogin={() => setView("login")}
            />
          )}

          {view === "addplace" && (
            <AddPlace token={token} onDone={() => setView("home")} />
          )}

          {view === "state" && <Home showState={selectedState} />}
        </div>
      </div>
    </div>
  );
}

export default App;

/* ---- INLINE STYLES ---- */
const headerStyle = {
  background: "#e57373",
  color: "white",
  padding: "10px 20px",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
};

const linesStyle = {
  cursor: "pointer",
  display: "flex",
  flexDirection: "column",
  gap: "3px",
};

const lineStyle = {
  background: "white",
  height: "3px",
  width: "20px",
  borderRadius: "2px",
};
