import React, { useState, useEffect } from "react";

export default function States({ selectedState, onSelectState, onBack }) {
  const [states] = useState([
    "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh",
    "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand",
    "Karnataka", "Kerala", "Madhya Pradesh", "Maharashtra", "Manipur",
    "Meghalaya", "Mizoram", "Nagaland", "Odisha", "Punjab", "Rajasthan",
    "Sikkim", "Tamil Nadu", "Telangana", "Tripura", "Uttar Pradesh",
    "Uttarakhand", "West Bengal", "Delhi", "Puducherry", "Jammu and Kashmir",
    "Ladakh",
  ]);

  const [currentState, setCurrentState] = useState(selectedState || null);
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    if (currentState) {
      fetch(`http://localhost:8000/places?state=${encodeURIComponent(currentState)}`)
        .then((r) => r.json())
        .then(setPlaces)
        .catch((e) => console.error("Error fetching places:", e));
    }
  }, [currentState]);

  // --- Return layout ---
  return (
    <div style={page}>
      <h1 style={heading}>Explore {currentState ? currentState : "Indian States"}</h1>

      {/* ---- 1️⃣ STATES GRID ---- */}
      {!currentState && (
        <div style={buttonGrid}>
          {states.map((st) => (
            <button
              key={st}
              style={stateButton}
              onClick={() => {
                setCurrentState(st);
                if (onSelectState) onSelectState(st);
              }}
              onMouseEnter={(e) => (e.target.style.background = "#0056b3")}
              onMouseLeave={(e) => (e.target.style.background = "#007bff")}
            >
              {st}
            </button>
          ))}
        </div>
      )}

      {/* ---- 2️⃣ PLACES GRID ---- */}
      {currentState && !selectedPlace && (
        <div style={placesSection}>
          <button
            style={backBtn}
            onClick={() => {
              setCurrentState(null);
              if (onBack) onBack();
            }}
          >
            ← Back to States
          </button>

          {places.length === 0 ? (
            <p style={{ textAlign: "center" }}>No places found for this state.</p>
          ) : (
            <div style={placesGrid}>
              {places.map((p) => (
                <div key={p.id} style={placeCard}>
                  {p.photos && p.photos[0] ? (
                    <img
                      src={`http://localhost:8000/uploads/${p.photos[0]}`}
                      alt={p.name}
                      style={photoThumb}
                    />
                  ) : (
                    <div style={noImage}>No Image</div>
                  )}
                  <h3 style={placeTitle}>{p.name}</h3>
                  <button
                    style={viewBtn}
                    onClick={() => setSelectedPlace(p)}
                  >
                    View Details
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* ---- 3️⃣ SINGLE PLACE DETAIL ---- */}
      {selectedPlace && (
        <div style={placeDetail}>
          <button
            style={backBtn}
            onClick={() => setSelectedPlace(null)}
          >
            ← Back to {currentState}
          </button>

          <h2 style={detailTitle}>{selectedPlace.name}</h2>

          {/* Image gallery */}
          <div style={photoContainer}>
            {selectedPlace.photos && selectedPlace.photos.length > 0 ? (
              selectedPlace.photos.map((photo, index) => (
                <img
                  key={index}
                  src={`http://localhost:8000/uploads/${photo}`}
                  alt={selectedPlace.name}
                  style={photoLarge}
                />
              ))
            ) : (
              <div style={noImage}>No Images</div>
            )}
          </div>

          <p style={detailDesc}>{selectedPlace.description}</p>
        </div>
      )}
    </div>
  );
}

/* ---- INLINE STYLES ---- */
const page = {
  padding: "20px",
  background: "#f9fafc",
  minHeight: "100vh",
};

const heading = {
  textAlign: "center",
  fontSize: "2em",
  fontWeight: "bold",
  color: "#222",
  marginBottom: "20px",
};

const buttonGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
  gap: "15px",
};

const stateButton = {
  background: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "8px",
  padding: "12px 20px",
  cursor: "pointer",
  fontWeight: "500",
  transition: "0.3s",
};

const placesSection = {
  textAlign: "center",
};

const backBtn = {
  marginBottom: "15px",
  padding: "8px 16px",
  background: "#ff5722",
  color: "white",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
};

const placesGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
  gap: "20px",
  marginTop: "20px",
};

const placeCard = {
  background: "white",
  borderRadius: "10px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  padding: "12px",
  textAlign: "center",
  transition: "transform 0.3s ease",
};
placeCard[":hover"] = { transform: "translateY(-4px)" };

const photoThumb = {
  width: "100%",
  height: "140px",
  borderRadius: "8px",
  objectFit: "cover",
  marginBottom: "10px",
};

const viewBtn = {
  background: "#007bff",
  color: "#fff",
  border: "none",
  padding: "6px 12px",
  borderRadius: "6px",
  cursor: "pointer",
  fontSize: "0.9em",
  marginTop: "8px",
};

const noImage = {
  background: "#ddd",
  height: "140px",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  borderRadius: "8px",
  color: "#555",
};

const placeTitle = {
  fontSize: "1.1em",
  fontWeight: "bold",
  color: "#333",
};

const placeDetail = {
  textAlign: "center",
  background: "white",
  padding: "20px",
  borderRadius: "10px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
};

const detailTitle = {
  fontSize: "1.6em",
  color: "#004080",
  marginBottom: "10px",
};

const photoContainer = {
  display: "flex",
  overflowX: "auto",
  gap: "10px",
  padding: "10px 0",
  scrollSnapType: "x mandatory",
};

const photoLarge = {
  flexShrink: 0,
  width: "320px",
  height: "220px",
  borderRadius: "10px",
  objectFit: "cover",
  scrollSnapAlign: "start",
};

const detailDesc = {
  textAlign: "justify",
  fontSize: "1em",
  color: "#555",
  marginTop: "15px",
  padding: "0 20px",
};
