import React from "react";

export default function Home({ onSelectState }) {
  const statesList = [
    "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh",
    "Goa", "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand", "Karnataka",
    "Kerala", "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya",
    "Mizoram", "Nagaland", "Odisha", "Punjab", "Rajasthan", "Sikkim",
    "Tamil Nadu", "Telangana", "Tripura", "Uttar Pradesh", "Uttarakhand",
    "West Bengal"
  ];

  return (
    <div style={container}>
      <h1 style={title}>Explore Indian States üó∫Ô∏è</h1>
      <p style={subtext}>
        Choose a state below to explore its famous tourist destinations.
      </p>

      <div style={grid}>
        {statesList.map((st) => (
          <button
            key={st}
            onClick={() => onSelectState(st)}
            style={button}
          >
            {st}
          </button>
        ))}
      </div>
    </div>
  );
}

/* ---- STYLES ---- */
const container = {
  textAlign: "center",
  padding: "30px",
};

const title = {
  fontSize: "26px",
  fontWeight: "bold",
  color: "#e53935",
};

const subtext = {
  margin: "10px 0 30px 0",
  fontSize: "16px",
  color: "#555",
};

const grid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(180px, 1fr))",
  gap: "12px",
  maxWidth: "900px",
  margin: "0 auto",
};

const button = {
  background: "linear-gradient(90deg, #ff7043, #e53935)",
  color: "white",
  border: "none",
  borderRadius: "10px",
  padding: "12px 10px",
  fontWeight: "bold",
  cursor: "pointer",
  boxShadow: "0 3px 6px rgba(0,0,0,0.2)",
  transition: "transform 0.2s",
};
