import React, {useEffect, useState} from 'react';
export default function Home({onSelectState, showState}){
  const [places, setPlaces] = useState([]);
  useEffect(()=>{ fetchPlaces(); },[]);
  function fetchPlaces(state){ 
    const url = state ? `http://localhost:8000/places?state=${encodeURIComponent(state)}` : 'http://localhost:8000/places';
    fetch(url).then(r=>r.json()).then(setPlaces).catch(e=>console.error(e));
  }
  useEffect(()=>{ if(showState) fetchPlaces(showState); },[showState]);
  // Minimal map: put a few positioned buttons representing states
  return (<div>
    <h1 style={{textAlign:'center'}}>Explore places</h1>
    <div className="map">
      <div style={{position:'absolute', left:40, top:20}} className="state-btn" onClick={()=>onSelectState('Karnataka')}>Karnataka</div>
      <div style={{position:'absolute', left:120, top:60}} className="state-btn" onClick={()=>onSelectState('Maharashtra')}>Maharashtra</div>
      <div style={{position:'absolute', left:200, top:40}} className="state-btn" onClick={()=>onSelectState('Tamil Nadu')}>Tamil Nadu</div>
      <div style={{position:'absolute', left:260, top:120}} className="state-btn" onClick={()=>onSelectState('Kerala')}>Kerala</div>
      <div style={{position:'absolute', left:420, top:80}} className="state-btn" onClick={()=>onSelectState('West Bengal')}>West Bengal</div>
      <div style={{position:'absolute', left:300, top:20}} className="state-btn" onClick={()=>onSelectState('Rajasthan')}>Rajasthan</div>
    </div>
    <div style={{marginTop:18}} className="places-grid">
      {places.map(p=> (<div key={p.id} className="card"><h3>{p.name}</h3><div>{p.state}</div><p>{p.description?.slice(0,120)}</p>{p.photos && p.photos[0] && <img src={`http://localhost:8000/uploads/${p.photos[0]}`} style={{width:'100%', height:120, objectFit:'cover'}} alt=""/>}</div>))}
    </div>
  </div>)
}
