import React, {useState} from 'react';
export default function Signup({onSignup}){
  const [username, setUsername] = useState(''); const [email, setEmail]=useState(''); const [password, setPassword]=useState(''); const [file, setFile]=useState(null); const [err, setErr]=useState('');
  function submit(e){ e.preventDefault(); setErr(''); const fd = new FormData(); fd.append('username', username); fd.append('email', email); fd.append('password', password); if(file) fd.append('file', file); fetch('http://localhost:8000/signup',{method:'POST', body:fd}).then(r=>r.json()).then(data=>{ if(data.detail) setErr(data.detail); else onSignup(data.token); }).catch(()=>setErr('Network error')); }
  return (<div style={{maxWidth:420}}><h2>Signup</h2><form onSubmit={submit}><input placeholder='username' value={username} onChange={e=>setUsername(e.target.value)}/><input placeholder='email' value={email} onChange={e=>setEmail(e.target.value)}/><input placeholder='password' type='password' value={password} onChange={e=>setPassword(e.target.value)}/><label>Profile photo (0-2MB)</label><input type='file' onChange={e=>setFile(e.target.files[0])}/><button className='btn' type='submit'>Signup</button>{err && <div style={{color:'red'}}>{err}</div>}</form></div>)
}
