import React, { useEffect, useState } from "react";

export default function States({ selectedState, onSelectState, onBack }) {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    fetchPlaces();
  }, [selectedState]);

  async function fetchPlaces() {
    try {
      const url = selectedState
        ? `http://localhost:8000/places?state=${selectedState}`
        : "http://localhost:8000/places";
      const res = await fetch(url);
      const data = await res.json();
      setPlaces(data);
    } catch {
      console.error("Failed to fetch places");
    }
  }

  if (selectedPlace) {
    return (
      <div style={detailContainer}>
        <button style={backBtn} onClick={() => setSelectedPlace(null)}>
          ‚Üê Back
        </button>
        <h2 style={{ color: "white", marginBottom: "20px" }}>
          {selectedPlace.name}
        </h2>
        <img
          src={`http://localhost:8000/uploads/${selectedPlace.image}`}
          alt={selectedPlace.name}
          style={detailImage}
        />
        <p style={{ color: "#ddd", marginTop: "20px", maxWidth: "700px" }}>
          {selectedPlace.description}
        </p>
      </div>
    );
  }

  return (
    <div style={page}>
      <h2 style={title}>
        {selectedState ? `Explore ${selectedState}` : "Explore Indian Places"}
      </h2>

      <div style={cardGrid}>
        {places.length === 0 ? (
          <p style={{ color: "#ccc" }}>No places found.</p>
        ) : (
          places.map((p) => (
            <div key={p.id} style={card} onClick={() => setSelectedPlace(p)}>
              <div style={imageBox}>
                <img
                  src={`http://localhost:8000/uploads/${p.image}`}
                  alt={p.name}
                  style={image}
                />
                <div style={tag}>{p.state}</div>
              </div>

              <div style={contentBox}>
                <h3 style={cardTitle}>{p.name}</h3>
                <p style={cardDesc}>
                  {p.description?.slice(0, 60) || "No description"}...
                </p>
                <div style={bottomRow}>
                  <div style={iconRow}>
                    <span style={{ marginRight: "6px" }}>üìç</span>
                    <span>{p.state}</span>
                  </div>
                  <div style={rating}>‚≠ê 5.0</div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

/* ---- STYLES ---- */

const page = {
  background: "#1c1b29",
  color: "white",
  minHeight: "100vh",
  padding: "30px",
};

const title = {
  textAlign: "center",
  fontSize: "26px",
  fontWeight: "bold",
  marginBottom: "30px",
  color: "#f5f5f5",
};

const cardGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
  gap: "25px",
  maxWidth: "1100px",
  margin: "0 auto",
};

const card = {
  background: "#2a2940",
  borderRadius: "10px",
  overflow: "hidden",
  boxShadow: "0 4px 12px rgba(0,0,0,0.3)",
  cursor: "pointer",
  transition: "transform 0.25s",
};
card["&:hover"] = {
  transform: "scale(1.03)",
};

const imageBox = {
  position: "relative",
  width: "100%",
  height: "160px",
  overflow: "hidden",
};

const image = {
  width: "100%",
  height: "100%",
  objectFit: "cover",
};

const tag = {
  position: "absolute",
  top: "10px",
  left: "10px",
  background: "#5138ee",
  color: "white",
  padding: "4px 10px",
  fontSize: "12px",
  borderRadius: "4px",
  fontWeight: "bold",
};

const contentBox = {
  padding: "15px",
};

const cardTitle = {
  fontSize: "18px",
  fontWeight: "bold",
  color: "#fff",
  marginBottom: "8px",
};

const cardDesc = {
  fontSize: "14px",
  color: "#ccc",
  marginBottom: "10px",
};

const bottomRow = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  fontSize: "13px",
  color: "#aaa",
};

const iconRow = {
  display: "flex",
  alignItems: "center",
};

const rating = {
  color: "#ffcc00",
  fontWeight: "bold",
};

const detailContainer = {
  background: "#1c1b29",
  color: "white",
  textAlign: "center",
  minHeight: "100vh",
  padding: "40px",
};

const detailImage = {
  width: "70%",
  maxWidth: "600px",
  borderRadius: "12px",
  boxShadow: "0 6px 15px rgba(0,0,0,0.4)",
  objectFit: "cover",
};

const backBtn = {
  background: "#5138ee",
  color: "white",
  border: "none",
  padding: "8px 12px",
  borderRadius: "6px",
  cursor: "pointer",
  marginBottom: "20px",
};
