import React from "react";

export default function Home() {
  const imageUrl =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR0UKmmu2SO6bjD6Gfb-a-gCppLgQhgXRf16_WeN97UoplZfC2Rdew1A4bCnoFwj9yQVLcz6iUCI-WxTEF7nzq5nzOiSlRn_G50Ol99PNOurg&s=10";

  return (
    <div style={container}>
      {/* Marquee text at the top */}
      <marquee style={marqueeStyle} behavior="scroll" direction="left">
        üåç Welcome to the Travel Explorer ‚Äî Discover Beautiful Places Across the
        World! ‚ú®
      </marquee>

      {/* Background image */}
      <img src={imageUrl} alt="Home hero" style={heroImage} />
    </div>
  );
}

const container = {
  width: "100%",
  height: "100%",
  minHeight: "calc(100vh - 60px)", // adjust if you have header height
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  backgroundColor: "#000",
  overflow: "hidden",
  position: "relative", // ensure absolute marquee stays in place
};

const heroImage = {
  width: "100%",
  height: "100%",
  objectFit: "cover",
  objectPosition: "center",
};

const marqueeStyle = {
  color: "#fff",
  fontSize: "1.5rem",
  fontWeight: "bold",
  backgroundColor: "rgba(0, 0, 0, 0.6)",
  width: "100%",
  padding: "10px 0",
  position: "absolute",
  top: 0, // üëà now the marquee is at the top
  zIndex: 10, // ensure it stays above the image
};
