import React, {useState, useEffect} from 'react';
import Sidebar from './Sidebar';
import Home from './Home';
import Login from './Login';
import Signup from './Signup';
import AddPlace from './AddPlace';

function App(){
  const [view, setView] = useState('home');
  const [token, setToken] = useState(localStorage.getItem('token')||null);
  const [selectedState, setSelectedState] = useState(null);
  useEffect(()=>{
    if(token) localStorage.setItem('token', token);
    else localStorage.removeItem('token');
  },[token]);

  return (<div>
    <div className="header">
      <div>Welcome to Indian Tourism</div>
      <div style={{display:'flex', alignItems:'center', gap:12}}>
        <div className="three-lines" onClick={()=> alert('Sidebar toggled (mobile)')}>
          <div className="line"></div><div className="line"></div><div className="line"></div>
        </div>
        <div style={{fontSize:14}}>{token ? 'Logged in' : 'Not logged'}</div>
      </div>
    </div>
    <div className="container">
      <Sidebar onNavigate={setView} token={token} onLogout={()=>{setToken(null); setView('home')}}/>
      <div className="content">
        {view==='home' && <Home onSelectState={(s)=>{ setSelectedState(s); setView('state') }} selectedState={selectedState}/>}
        {view==='login' && <Login onLogin={(t)=>{ setToken(t); setView('home') }} />}
        {view==='signup' && <Signup onSignup={(t)=>{ setToken(t); setView('home') }} />}
        {view==='addplace' && <AddPlace token={token} onDone={()=>setView('home')} />}
        {view==='state' && <Home showState={selectedState} />}
      </div>
    </div>
  </div>);
}
export default App;
