import React, { useState } from "react";

export default function AddPlace({ token, onDone }) {
  const [name, setName] = useState("");
  const [state, setState] = useState("");
  const [desc, setDesc] = useState("");
  const [files, setFiles] = useState(null);
  const [err, setErr] = useState("");
  const [success, setSuccess] = useState("");

  async function submit(e) {
    e.preventDefault();
    setErr("");
    setSuccess("");

    if (!token) {
      setErr("Login required");
      return;
    }

    const fd = new FormData();
    fd.append("name", name);
    fd.append("state", state);
    fd.append("description", desc);
    fd.append("token", token);
    if (files) {
      for (let i = 0; i < files.length; i++) fd.append("files", files[i]);
    }

    try {
      const res = await fetch("http://localhost:8000/places", {
        method: "POST",
        body: fd,
      });
      const data = await res.json();
      if (!res.ok) setErr(data.detail || "Failed");
      else {
        setSuccess("Place added successfully!");
        setName("");
        setState("");
        setDesc("");
        setFiles(null);
        onDone && onDone();
      }
    } catch {
      setErr("Network error");
    }
  }

  return (
    <div style={{ display: "flex", justifyContent: "center", height: "80vh" }}>
      <div style={card}>
        <h2 style={{ color: "white" }}>Add New Place</h2>
        <form onSubmit={submit}>
          <input
            type="text"
            placeholder="Place Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={input}
          />
          <input
            type="text"
            placeholder="State"
            value={state}
            onChange={(e) => setState(e.target.value)}
            required
            style={input}
          />
          <textarea
            placeholder="Description"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
            style={{ ...input, height: "80px" }}
          />
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={(e) => setFiles(e.target.files)}
            style={input}
          />
          <button type="submit" style={button}>
            Add Place
          </button>
          {err && <div style={{ color: "yellow" }}>{err}</div>}
          {success && <div style={{ color: "#00ff99" }}>{success}</div>}
        </form>
      </div>
    </div>
  );
}

const card = {
  background: "linear-gradient(135deg, #ff9966, #ff5e62)",
  padding: "40px",
  borderRadius: "12px",
  boxShadow: "0 8px 20px rgba(0,0,0,0.2)",
  width: "400px",
  textAlign: "center",
};
const input = {
  width: "100%",
  padding: "10px",
  margin: "8px 0",
  borderRadius: "6px",
  border: "none",
  outline: "none",
};
const button = {
  ...input,
  background: "#fff",
  color: "#333",
  cursor: "pointer",
  fontWeight: "bold",
};
