import React, { useState, useEffect } from "react";
import Sidebar from "./Sidebar";
import Home from "./Home";
import Login from "./Login";
import Signup from "./Signup";
import AddPlace from "./AddPlace";
import States from "./States";

function App() {
  const [view, setView] = useState("home");
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [selectedState, setSelectedState] = useState(null);

  // persist token
  useEffect(() => {
    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [token]);

  return (
    <div style={{ height: "100vh", display: "flex", flexDirection: "column" }}>
      {/* HEADER */}
      <div style={headerStyle}>
        <div style={{ fontWeight: "bold", fontSize: 22 }}>
          üáÆüá≥ Welcome to Indian Tourism
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <div
            style={linesStyle}
            onClick={() => alert("Sidebar toggle (for mobile view)")}
          >
            <div style={lineStyle}></div>
            <div style={lineStyle}></div>
            <div style={lineStyle}></div>
          </div>
          <div style={{ fontSize: 14, fontWeight: 500 }}>
            {token ? "‚úÖ Logged in" : "‚ùå Guest Mode"}
          </div>
        </div>
      </div>

      {/* BODY */}
      <div style={{ display: "flex", flex: 1, overflow: "hidden" }}>
        <Sidebar
          onNavigate={setView}
          token={token}
          onLogout={() => {
            setToken(null);
            setView("home");
          }}
        />

        {/* MAIN CONTENT */}
        <div
          style={{
            flex: 1,
            padding: "20px",
            background: "#f5f5f5",
            overflowY: "auto",
          }}
        >
          {/* HOME PAGE */}
          {view === "home" && (
            <Home
              onSelectState={(s) => {
                setSelectedState(s);
                setView("states");
              }}
            />
          )}

          {/* STATES PAGE */}
          {view === "states" && (
            <States
              onSelectState={(s) => {
                setSelectedState(s);
              }}
            />
          )}

          {/* ADD PLACE PAGE */}
          {view === "addplace" && (
            <AddPlace token={token} onDone={() => setView("home")} />
          )}

          {/* SIGNUP PAGE */}
          {view === "signup" && (
            <Signup
              onSignup={(t) => {
                setToken(t);
                setView("home");
              }}
              onSwitchToLogin={() => setView("login")}
            />
          )}

          {/* LOGIN PAGE */}
          {view === "login" && (
            <Login
              onLogin={(t) => {
                setToken(t);
                setView("home");
              }}
              onSwitchToSignup={() => setView("signup")}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;

/* ---- INLINE STYLES ---- */
const headerStyle = {
  background: "linear-gradient(90deg, #e53935, #ff7043)",
  color: "white",
  padding: "10px 20px",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
};

const linesStyle = {
  cursor: "pointer",
  display: "flex",
  flexDirection: "column",
  gap: "3px",
};

const lineStyle = {
  background: "white",
  height: "3px",
  width: "20px",
  borderRadius: "2px",
};
