import React, { useState } from "react";

export default function AddPlace({ token, onDone }) {
  const [name, setName] = useState("");
  const [state, setState] = useState("");
  const [desc, setDesc] = useState("");
  const [files, setFiles] = useState(null);
  const [err, setErr] = useState("");
  const [success, setSuccess] = useState("");

  async function submit(e) {
    e.preventDefault();
    setErr("");
    setSuccess("");

    if (!token) {
      setErr("Login required");
      return;
    }

    const fd = new FormData();
    fd.append("name", name);
    fd.append("stateId", state);
    fd.append("description", desc);
    if (files) {
      for (let i = 0; i < files.length; i++) {
        if (files[i].size > 2 * 1024 * 1024) {
          setErr("Each file must be less than 2MB");
          return;
        }
        fd.append("photos", files[i]);
      }
    }

    try {
      const res = await fetch("http://localhost:5000/api/places", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: fd,
      });
      const data = await res.json();
      if (data.error) setErr(data.error);
      else {
        setSuccess("Place added successfully!");
        setName("");
        setState("");
        setDesc("");
        setFiles(null);
        onDone && onDone();
      }
    } catch (e) {
      setErr("Network error");
    }
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "80vh",
      }}
    >
      <div
        style={{
          background: "linear-gradient(135deg, #ff9966, #ff5e62)",
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 8px 20px rgba(0,0,0,0.2)",
          width: "400px",
          textAlign: "center",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "20px" }}>Add New Place</h2>

        <form onSubmit={submit}>
          <input
            type="text"
            placeholder="Place Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={inputStyle}
          />
          <input
            type="text"
            placeholder="State"
            value={state}
            onChange={(e) => setState(e.target.value)}
            required
            style={inputStyle}
          />
          <textarea
            placeholder="Description"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
            style={{ ...inputStyle, height: "80px", resize: "none" }}
          />
          <label style={{ color: "white", fontSize: "14px" }}>
            Photos (0â€“2MB each)
          </label>
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={(e) => setFiles(e.target.files)}
            style={{
              ...inputStyle,
              background: "white",
              border: "none",
              color: "#333",
            }}
          />
          <button
            type="submit"
            style={{
              ...inputStyle,
              background: "#fff",
              color: "#333",
              cursor: "pointer",
              fontWeight: "bold",
              marginTop: "10px",
            }}
          >
            Add Place
          </button>

          {err && <div style={{ color: "yellow", marginTop: "8px" }}>{err}</div>}
          {success && (
            <div style={{ color: "#00ff99", marginTop: "8px" }}>{success}</div>
          )}
        </form>
      </div>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  padding: "10px",
  margin: "8px 0",
  borderRadius: "6px",
  border: "none",
  outline: "none",
};
